import styled from 'styled-components';



export const ResumeContainer = styled.div ` 
   background-color: #ddd8ff;
`

export const ResumeWrapper = styled.div `
    display: grid;
    margin-top: 100px;
    margin-bottom: 20px;
    margin-left: auto;
    margin-right: auto;
    color: black;
    background: white;
    box-shadow: 0 5px 5px 0 rgba(0,0,0,0.14), 0 1px 5px 0 rgba(0,0,0,0.12), 0 3px 1px -2px rgba(0,0,0,0.2);
    //height: 85vh;
    padding: 30px;
    width: 60vw;
    max-width: 20cm;
    max-height: 29.7cm;
    grid-template-columns: 1.5fr 1fr 1.5fr;
    grid-template-rows:  4fr 4fr 1fr 4.5fr 1fr 2fr 1fr 2.2fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr 1fr;;
    grid-template-areas: 
    "'.'  'contact' '.'"
    "'bio' 'bio' 'bio'"
    "workExpTitle               .                   ."
    "workExpData                workExpData         workExpData"
    "TVExpTitle                 .                   ."
    "TVExpData                  TVExpData           TVExpData"
    "commercialExpTitle         .                   ."
    "commercialExpData          commercialExpData   commercialExpData"
    "theatreExpTitle            .                   ."
    "theatreExpData             theatreExpTitle     theatreExpTitle "
    "workshopsExpTitle          .                   ."
    "workshopsExpData           workshopsExpData    workshopsExpData"
    "weddingExpTitle            .                   ."
    "weddingExpData             weddingExpData      weddingExpData"
    "fashionExpTitle            .                   ."
    "fashionExpData             fashionExpData      fashionExpData"
    "videogamesExpTitle         .                   ."
    "videogamesExpData          videogamesExpData   videogamesExpData"
    "qualifications             qualifications      qualifications"
    "closing                    closing             closing";

@media screen and (max-width: 768px) {
    padding: 15px;
}
`

export const ResumeContact = styled.div `
    text-align: center;
    justify-content: center;
    grid-area: contact;
    background: lightgreen;

`

export const ResumeBio = styled.div `
    text-align: center;
    grid-area: bio;
    background: lightgoldenrodyellow;
    height: 150px;
    font-style: normal;
    font-size: 12px;
    font-weight: 200; 
`

export const newLine = styled.div`
    white-space: pre-line;
`

export const WorkExperienceTitle = styled.div `
    text-align: left;
    justify-content: left;
    grid-area: workExpTitle;
    background: lightgreen;
`

export const WorkExperienceData = styled.div `
    text-align: center;
    grid-area: workExpData;

`
