import React, {useState, useEffect} from 'react';

import {
  Nav,
  NavLink,
  Bars,
  NavMenu,
  NavBtn,
  NavBtnLink,
  IconInsta,
  IconFB,
} from './navElements';



const Navbar = ({toggle}) => {

    const [scrollNav, setScrollNav] = useState(false)

    const changeNav = () => {
        if(window.scrollY >= 600) {
            setScrollNav(true)
        } else {
            setScrollNav(false)
        }
    }

    useEffect( () => {
            if(window.location.pathname === '/'){
                window.addEventListener('scroll', changeNav)
            } else {
                setScrollNav(true)
            }
        }, [])

    return (
        <>
            <Nav scrollNav={scrollNav}>
                <NavLink to='/'>
                <h1>Logo img tag</h1>
                </NavLink>
                <Bars onClick={toggle}/>

                <NavMenu>
                    <NavLink to="/" activeStyle exact>Home</NavLink>
                    <NavLink to="/resume" activeStyle exact strict> Resume </NavLink>
                    <NavLink to="/portfolio" activeStyle>Portfolio</NavLink>
                    <NavLink to="/contact" activeStyle>Contact</NavLink>
                </NavMenu>

                <NavBtn>
                    <IconFB />
                    <IconInsta/>
                </NavBtn>

            </Nav>

        </>
    );
};

export default Navbar;
