import React from 'react';
import LightGallery from 'lightgallery/react';
import Image from '../components/portfolioImgs';

// import styles
import 'lightgallery/css/lightgallery.css';
import 'lightgallery/css/lg-zoom.css';
import 'lightgallery/css/lg-thumbnail.css';

// import plugins if you need
import lgThumbnail from 'lightgallery/plugins/thumbnail';
import lgZoom from 'lightgallery/plugins/zoom';

const Portfolio = () => {
  const onInit = () => {
    console.log('lightGallery has been initialized');
};

function importAll(r) {
  let images = {};
  r.keys().map((item, index) => { images[item.replace('./', '')] = r(item); });
  return images;
}

const images = importAll(require.context('../assets/images', false, /\.(png|jpe?g|svg)$/));

const $dynamicGallery = document.getElementById("dynamic-gallery");
const dynamicGallery = window.lightGallery($dynamicGallery, {
  dynamic: true,
  plugins: [lgZoom, lgThumbnail],
  dynamicEl: [
      {
        src: "",
        thumb: "",
        subHtml: `<div class="lightGallery-captions"><h4>Name: </h4> <p>Description: </p> </div>`
      }
    ]

  });

  $dynamicGallery.addEventListener("click", () => {
    dynamicGallery.openGallery(0);
  });



  return (
    <div className="dynamic-Gallery">
        <LightGallery
            onInit={onInit}
            speed={500}
            plugins={[lgThumbnail, lgZoom]}

        >

            <a href="img/img2.jpg">
                <img alt=".." src="img/thumb2.jpg" />
            </a>
            ...
        </LightGallery>
    </div>
  );
};

export default Portfolio;
