import React, {useState, useRef}  from 'react';
import Dropdown from '../../components/dropdown/dropdown';
import Navbar from '../../components/nav/navbar';
import Footer from '../../components/footer/index';
import emailjs from 'emailjs-com';

import './contact.css';

const Contact = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => {
    setIsOpen(!isOpen);
  }

  const form = useRef();

  const [name, setName] = useState('');
  const [email, setEmail] = useState('');
  const [message, setMessage] = useState('');
  const [emailSent, setEmailSent] = useState(false);

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('gmail', 'my-first-template', form.current, 'user_sA3tlOdalK12Wt9JkvHaZ')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
      e.target.reset();
  };

  return (
    <>
      <Dropdown isOpen={isOpen} toggle={toggle} />
      <Navbar toggle={ toggle } />

      <div className="body">
        <input type="text" placeholder="Name/Company" value="name" />
        <input type="email" placeholder="Email Address" value="email"  />
        <input type="text" placeholder="Subject" value="subject" />

        <textarea placeholder="Your message" value="message"></textarea>
        <button onClick={sendEmail}> Send Message </button>
      </div>

      <Footer />
    </>
  ) 
};

export default Contact;
