import React, {useState, useRef}  from 'react';
import Dropdown from '../../components/dropdown/dropdown';
import Navbar from '../../components/nav/navbar';
import Footer from '../../components/footer/index';
import emailjs from 'emailjs-com';
import { Player, Controls } from '@lottiefiles/react-lottie-player';


import './contact.css';

const Result = () => {
  return(

    <>
      <p>Your message has successfully been sent! We will contact you soon. </p>
      <Player

          lottieRef={instance => {
          this.setState({ lottie: instance }); // the lottie instance is returned in the argument of this prop. set it to your local state
        }}
          autoplay={false}
          loop={true}
          controls={false}
          src="https://assets3.lottiefiles.com/packages/lf20_XZ3pkn.json"
          style={{ height: '100px', width: '100px' }}
      >
      </Player>
    </>
  );
};



const Contact = () => {
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => {
    setIsOpen(!isOpen);
  }
  const form = useRef();

  const [result, showResult] = useState(false);

  const sendEmail = (e) => {
    e.preventDefault();

    emailjs.sendForm('service_2npcj2s', 'my-first-template', e.target, 'user_sA3tlOdalK12Wt9JkvHaZ')
      .then((result) => {
          console.log(result.text);
      }, (error) => {
          console.log(error.text);
      });
      e.target.reset();
      showResult(true);
  };

  return (
    <>
      <Dropdown isOpen={isOpen} toggle={toggle} />
      <Navbar toggle={ toggle } />

      <div className="body">
        <div className="contactMe">
          <div className="contactOverlay">
            <div className="container">
              <div className="form">

                <form action="" onSubmit={sendEmail}>
                  <div className="formWord">
                    <span> Name / Company </span>
                    <input type="text" placeholder="Name/Company" name="name" required />
                    <span> Email</span>
                    <input type="email" placeholder="Email Address" name="email" required  />
                    <span> Subject </span>
                    <input type="text" placeholder="Subject" name="subject" required/>
                  </div>
                  <div className="formWord">
                    <span> Message</span>
                    <textarea placeholder="Your message" name="message" required></textarea>
                    <button> Send Message </button>

                    <div className="submittedForm">
                      {result ? <Result/> : null }
                    </div>

                  </div>
                </form>
              </div>
            </div>
          </div>
        </div>
      </div>

      <Footer />
    </>
  ) 
};

export default Contact;
