import styled from 'styled-components';
import { FaTimes, FaFacebook, FaInstagram} from 'react-icons/fa';
import { Link as LinkS } from 'react-scroll';
import { Link as LinkR } from 'react-router-dom'


export const DropdownContainer = styled.aside `
    position: fixed;
    z-index: 999;
    width: 100%;
    height: 100%;
    background: #e8c4ff;;
    display: grid;
    align-items: center;
    top: 0;
    left: 0;
    transition: 0.3s ease-in-out;
    opacity: ${({ isOpen }) => (isOpen ? '100%' : '0' )};
    top: ${({ isOpen }) => (isOpen ? '0' : '-100%')};
`

export const CloseIcon = styled(FaTimes) `
    color: #fff;

`

export const Icon = styled.div `
    position: absolute;
    top: 1.2rem;
    right: 1.5rem;
    background: transparent;
    font-size: 2rem;
    cursor: pointer;
    outline: none;
`
export const DropdownWrapper = styled.div `
    color: gray;
`

export const DropdownMenu = styled.ul `
    display: grid;
    grid-template-columns: 1fr;
    grid-template-rows: repeat(6, 80px);
    text-align: center;

    @media screen and (max-width: 480px) {
        grid-template-rows: repeat(6, 60px);
    }
`

export const DropdownLink = styled(LinkS) `
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    text-decoration: none;
    list-style: none;
    transition: 0.2s ease-in-out;
    color: #fff;

    &.active {
        text-decoration-line: underline;
        text-decoration-style: solid;
        text-underline-offset: 5px;
        padding-bottom: .3rem;
        color: #fff0d8;

    &:hover {
        color: yellow;
        transition: 0.2s ease-in-out;
        text-decoration-line: underline;
        text-decoration-style: wavy;
        padding-bottom: .3em;


    }
`

export const DropdownBtnWrap = styled.div `
    display: flex;
    justify-content: center;
`

export const DropdownRoute = styled(LinkR)`
    border-radius: 50px;
    background: #01bf71;
    white-space: nowrap;
    padding: 5px;
    color: #010606;
    font-size: 12px;
    outline: none;
    border: none;
    cursor: pointer;
    transition: all 0.2s ease-in-out;
    text-decoration: none;

    &:hover {
        background: #fff
        color: #0100606;
    }
`
