import React from 'react';
//import LightGallery from 'lightgallery/react';
import {LightgalleryItem, LightgalleryProvider} from "Lightgallery/lightgallery";
import {galleryImgDataGroup1, galleryImgDataGroup2} from '../components/galleryImgData'


// import styles
import 'lightgallery/css/lightgallery.css';
import 'lightgallery/css/lg-zoom.css';
import 'lightgallery/css/lg-thumbnail.css';

// import plugins if you need
import lgThumbnail from 'lightgallery/plugins/thumbnail';
import lgZoom from 'lightgallery/plugins/zoom';
import lgShare  from 'lightgallery/plugins/share';
import lgRotate from "lightgallery/plugins/rotate";

const settings = {
  speed: 200,
  download: false,
  plugins: [lgZoom, lgThumbnail, lgRotate, lgShare]
};

const Portfolio = () => {
  const onInit = () => {
    console.log('lightGallery has been initialized');
};

const PhotoItem = ({ image, group }) => (
  <div style={{ maxWidth: "250px", width: "200px", padding: "5px" }}>
    <LightgalleryItem group={group} src={image}>
      <img src={image} style={{ width: "100%" }} />
    </LightgalleryItem>
  </div>
);

  return (
    <LightgalleryProvider>
    <h1 style={{ textAlign: "center" }}>Group 1</h1>
    <div style={{ display: "flex", alignItems: "center" }}>
      {galleryImgDataGroup1.map((p, idx) => (
        <PhotoItem key={idx} image={p} group="group1" />
      ))}
    </div>
    <h1 style={{ textAlign: "center" }}>Group 2</h1>
    <div style={{ display: "flex", alignItems: "center" }}>
      {galleryImgDataGroup2.map((p, idx) => (
        <PhotoItem key={idx} image={p} group="group2" />
      ))}
    </div>
  </LightgalleryProvider>
  );
};

export default Portfolio;
