import React, {useState}  from 'react';
import Dropdown from '../../components/dropdown/dropdown';
import Navbar from '../../components/nav/navbar';
import Footer from '../../components/footer/index';
import LightGallery from 'lightgallery/react';
import {galleryImgDataGroup1 } from './galleryImgData';
import {LightGalleryContainer, PortfolioContainer, AlbumContainer} from './portfolioElements';
//import InfiniteLoading from 'react-simple-infinite-loading' REMOVE THIS FROM YARN/DEPENDENCIES
import './portfolioStyles.css';

// import styles
import 'lightgallery/css/lightgallery.css';
import 'lightgallery/css/lg-zoom.css';
import 'lightgallery/css/lg-thumbnail.css';

// import plugins if you need
import lgThumbnail from 'lightgallery/plugins/thumbnail';
import lgZoom from 'lightgallery/plugins/zoom';
import lgRotate  from 'lightgallery/plugins/rotate';

const Portfolio = (hasMore, fetchMore) => {
  const [isOpen, setIsOpen] = useState(false);

  const toggle = () => {
    setIsOpen(!isOpen);
  }

  const [items, setItems] = useState(galleryImgDataGroup1);

  const onInit = () => {
    console.log('lightGallery has been initialized');
  };

  return (
      <>
        <Dropdown isOpen={isOpen} toggle={toggle} />
        <Navbar toggle={ toggle } />
        <PortfolioContainer>
          <AlbumContainer>
            {<h3> HELLO </h3>}
            <LightGalleryContainer>

                <LightGallery onInit={onInit} plugins={[lgThumbnail, lgZoom, lgRotate ]} mode="lg-slide" elementClassNames="animated-thumbnails-gallery">
                  {items.map((item) => {
                    return (
                        <a  key={item.id} className="gallery-item" href={item.src} data-lg-size={item.size} data-sub-html={item.subHtml} >
                          <img className="img-responsive" src={item.thumb} alt={""} /> 
                        </a>           
                    )})
                    }
                </LightGallery>
            </LightGalleryContainer>
          </AlbumContainer>
        </PortfolioContainer>
        <Footer />
      </>
  );
};

export default Portfolio;
