export const IconInsta = styled(FaInstagram) `
border-radius: 4px;
outline: none;
border: none;
cursor: pointer;
transition: all 0.2s ease-in-out;
text-decoration: none;

margin-left: 24px;

&:hover {
  transition: all 0.2s ease-in-out;
  color: #e4405f;
  margin-bottom: .3em;
`;
