import React,  { useCallback, useEffect, useState } from 'react';
import LightGallery from 'lightgallery/react';
//import { LightGallerySettings } from 'lightgallery/lg-settings';
import {galleryImgDataGroup1, galleryImgDataGroup2} from '../components/galleryImgData'

// import styles
import 'lightgallery/css/lightgallery.css';
import 'lightgallery/css/lg-zoom.css';
import 'lightgallery/css/lg-thumbnail.css';

// import plugins if you need
import lgThumbnail from 'lightgallery/plugins/thumbnail';
import lgZoom from 'lightgallery/plugins/zoom';
import lgRotate  from 'lightgallery/plugins/rotate';
import lightGallery from 'lightgallery';

const Portfolio = () => {
  const [items, setItems] = useState(galleryImgDataGroup1);

  const onInit = () => {
    console.log('lightGallery has been initialized');
  };

  return (
      <LightGallery plugins={[lgThumbnail, lgZoom, lgRotate ]}  mode="lg-fade" onInit={onInit} speed={500} elementClassNames="animated-thumbnails">
        {items.map((item) => {
          return (
            <div key={item.id}  data-src={item.src} data-lg-size={item.size} className="gallery-item">
              <img className="img-responsive" src={item.thumb} alt={item.subHtml} width={300} height={100} /> 
              {console.log(item.src, item.id, item.subHtml)}
            </div>
          )})
          }
      </LightGallery>
  );
};

export default Portfolio;
