import React,  { useCallback, useEffect, useState } from 'react';
import LightGallery from 'lightgallery/react';
//import { LightGallerySettings } from 'lightgallery/lg-settings';
//import {galleryImgDataGroup1, galleryImgDataGroup2} from '../components/galleryImgData'

// import styles
import 'lightgallery/css/lightgallery.css';
import 'lightgallery/css/lg-zoom.css';
import 'lightgallery/css/lg-thumbnail.css';

// import plugins if you need
import lgThumbnail from 'lightgallery/plugins/thumbnail';
import lgZoom from 'lightgallery/plugins/zoom';
import lgRotate  from 'lightgallery/plugins/rotate';
import lightGallery from 'lightgallery';
import { template } from '@babel/core';

const Portfolio = () => {
  const galleryImgDataGroup1 = [
    {
        id: '1',
        size:"2400-3600",
        src: require('../assets/images/portfolio/album1/1.jpg').default,
        thumb: require('../assets/images/portfolio/album1/thumbs/1.jpg').default,
        subHtml: `<div class="lightGallery-captions">
        <h4>Photo by <a href="">Garrett Jackson</a></h4>
        <p>Published on May 8, 2020</p></div>`
    },
    {
        id: '2',
        size: "2400-3600",
        src: require('../assets/images/portfolio/album1/2.jpg').default,
        thumb: require('../assets/images/portfolio/album1/thumbs/2.jpg').default,
        subHtml: `<div class="lightGallery-captions">
        <h4>Photo by <a href="">Garrett Jackson</a></h4>
        <p>Published on May 8, 2020</p></div>`
    }];

  const [items, setItems] = useState(galleryImgDataGroup1);

  const onInit = () => {
    console.log('lightGallery has been initialized');
  };



  return (
      <LightGallery onInit={onInit} plugins={[lgThumbnail, lgZoom, lgRotate ]} mode="lg-fade" speed={500} elementClassNames="animated-thumbnails">
        {items.map((item) => {
          return (
            <div key={item.id} className="gallery-item">
              <a href={item.src}>
                <img className="img-responsive" src={item.thumb} alt={item.subHtml} /> 
              </a>

              {console.log(item.id, item.src, item.size)}
            </div>
          )})
          }
      </LightGallery>
  );
};

export default Portfolio;
