import React from 'react'
import AliceCarousel from 'react-alice-carousel';
import "react-alice-carousel/lib/alice-carousel.css";
import './carousel.css';
import one from     '../../pics/em1.jpg';
import two from     '../../pics/creative2.svg';
import three from   '../../pics/em2.jpg';
import four from    '../../pics/makeup.svg';
import five from    '../../pics/film.svg';
import { FaArrowRight, FaArrowLeft } from 'react-icons/fa';

const Carousel = () => {

    const responsive = {
        0: {items: 1},
        568: {items: 2},
        1024: {items: 3},
    };

    const items = [
        <img src={one} alt='' className='sliderimg'/> ,
        <img src={two} alt='' className='sliderimg'/> ,
        <img src={three} alt='' className='sliderimg'/> ,
        <img src={four} alt='' className='sliderimg'/> ,
        <img src={five} alt='' className='sliderimg'/> 
    ];

    const navItem = (item, i) => {
        return <i key={i} onClick={() => this.Carousel.slideTo(i)} />;
    };


    return (
        <AliceCarousel 
        items={items}
        autoPlay 
        infinite 
        responsive={responsive} 
        autoPlayInterval="2000" 
        autoPlayStrategy='none' 
        disableDotsControls
        touchTracking={true}
        disableButtonsControls
        rel={(el) => (this.Carousel = el)}
        />, 

        <nav>
            {items.map(navItem)}
        </nav>,

        <div>
            <button onClick={() => this.Carousel.slidePrev()}> <FaArrowLeft /> </button>
            <button onClick={() => Carousel.slideNext()}> <FaArrowRight /> </button>
        </div>


    )
}

export default Carousel
